(2037, 486, 'banner_text', '<div class="banner_text">Every day the world''s most trusted companies¬†put their faith in Omega</div>'),
(2038, 486, '_banner_text', 'field_55e4050f7824c'),
(2039, 486, 'desktop_banner', ''),
(2040, 486, '_desktop_banner', 'field_55ee5a5f939ea'),
(2041, 486, 'mobile_banner', '111'),
(2042, 486, '_mobile_banner', 'field_55ee5a5f939ea'),
(2043, 486, 'highlight_text', 'Every day many of the world''s most trusted companies put their faith in Omega to ensure the integrity of their global supply chains as they endeavor to work with partners who are ethical, secure and free from corrupt practices.'),
(2044, 486, '_highlight_text', 'field_58b652b6a7e58'),
(2045, 487, 'banner_text', '<div class="banner_text">Every day the world''s most trusted companies¬†put their faith in Omega</div>'),
