(2378, 534, '_form', '<div class=\"mailing-list-container\">\n	<h2>JOIN OUR MAILING LIST</h2>\n	<div class=\"col-12 col-md-12\">\n		<div class=\"row mb-4\">\n			<div class=\"col-md-6\">\n				[text* first-name  placeholder \"First Name*\"]\n			</div>\n			<div class=\"col-md-6\">\n				[text* last-name  placeholder \"Last Name*\"]\n			</div>\n		</div>\n		<div class=\"row mb-4\">\n			<div class=\"col-md-6\">\n				[text* company-name  placeholder \"Company Name*\"]\n			</div>\n			<div class=\"col-md-6\">\n				[text* job-title  placeholder \"Title*\"]\n			</div>\n		</div>\n		<div class=\"row mb-4\">\n			<div class=\"col-12 col-md-12\">\n				[email* your-email placeholder \"Email Address*\"]\n			</div>\n		</div>\n		<div class=\"row mb-4\">\n			<div class=\"col-12 col-md-12\">\n				[text your-address placeholder \"Address\"]\n			</div>\n		</div>\n		<div class=\"row mb-4\">\n			<div class=\"col-md-6\">\n				[text your-city placeholder \"City\"]\n			</div>\n			<div class=\"col-md-6\">\n				[text your-state placeholder \"State\"]\n			</div>\n		</div>\n		<div class=\"row mb-4\">\n			<div class=\"col-md-6\">\n				[text zip-code placeholder \"Zip Code\"]\n			</div>\n			<div class=\"col-md-6\">\n				[text your-country placeholder \"Country\"]\n			</div>\n		</div>\n		<div class=\"row\">\n			<div class=\"col-12 col-md-12\">\n				<div class=\"align-center\">\n					[submit \"SUBSCRIBE TO OUR NEWSLETTER\"]	\n				</div>\n			</div>\n		</div>\n		<div class=\"row mb-4\">\n			<div class=\"col-md-12\">\n				<span class=\"notes\">*Mandatory Fields</span>\n			</div>\n		</div>\n	</div>\n</div>'),
