(2397, 537, 'sg_popup_scripts', 'a:2:{s:2:\"js\";a:2:{s:15:\"sgpb-ShouldOpen\";s:673:\"function getCookie(cname) {\n  let name = cname + &quot;=&quot;;\n  let decodedCookie = decodeURIComponent(document.cookie);\n  let ca = decodedCookie.split(\';\');\n  for(let i = 0; i &lt; ca.length; i++) {\n    let c = ca[i];\n    while (c.charAt(0) == \' \') {\n      c = c.substring(1);\n    }\n    if (c.indexOf(name) == 0) {\n      return c.substring(name.length, c.length);\n    }\n  }\n  return &quot;&quot;;\n}\n\nlet closePopup= getCookie(&quot;closePopup&quot;);\nconsole.log(\'closePopup= \'+closePopup );\nif (closePopup == \'yes\') {\n    console.log(&quot;return false&quot;);\n    return false;\n}else if (closePopup == \'no\') {\n   console.log(&quot;return true&quot;);\n   return true;\n}\";s:13:\"sgpb-DidClose\";s:368:\"function setCookie(cname,cvalue,exdays) {\nconsole.log(&quot;setCookie&quot;);\n  const d = new Date();\n  d.setTime(d.getTime() + (exdays*24*60*60*1000));\n  let expires = &quot;expires=&quot; + d.toGMTString();\n  document.cookie = cname + &quot;=&quot; + cvalue + &quot;;&quot; + expires + &quot;;path=/&quot;;\n}\n\n setCookie(&quot;closePopup&quot;, &quot;yes&quot;, 30);\";}s:3:\"css\";s:1663:\".sg-popup-content{\n	border-radius:0!important;\n	background-color:#34373a!important;\n}\n\n.sgpb-theme-6-content{\n	box-shadow:none!important;\n}\n\n.mailing-list-container{\n	color:#FFF;\n}\n\n.mailing-list-container h2{\n	font-family: &quot;Oswald&quot;;\n    display: block;\n    width: 100%;\n    text-align: center;\n    font-weight: 500;\n    font-size: 4.5rem;\n    margin-bottom: 5rem;\n	color:#fff;\n}\n\n.mailing-list-container .row{\n	display:flex;\n	flex-wrap:wrap;\n}\n\n.mailing-list-container .mb-4{\n	margin-bottom:2.5rem!important;\n}\n\n.mailing-list-container .col-md-6 {\n    -ms-flex: 0 0 50%;\n    flex: 0 0 50%;\n    max-width: 50%;\n}\n\n.mailing-list-container .col-md-12{\n	-ms-flex: 0 0 1000%;\n    flex: 0 0 100%;\n    max-width: 100%;\n}\n\n.mailing-list-container input[type=&quot;text&quot;],\n.mailing-list-container input[type=&quot;email&quot;]{\n	background:none;\n	border: none;\n	color:#fff;\n	/*font-family: Lato,sans-serif;\n    font-weight: 300;*/\n    border-bottom: 1px solid #fff;\n	display:block;\n	width: 100%;\n	/*font-size:80%;*/\n}\n\n.mailing-list-container input:focus-visible{\n	outline:none;\n}\n\n.mailing-list-container input::placeholder {\n	color: #fff;\n}\n\n.mailing-list-container input[type=&quot;submit&quot;] {\n	background: none;\n    border: 1px solid #fff;\n    color: #fff;\n    padding: 5px 20px;\n    text-transform: uppercase;\n    font-family: &quot;Oswald&quot;;\n	display:flex;\n	margin:0 auto;\n}\n\n.mailing-list-container .align-center{\n	text-align:center;\n}\n\n.mailing-list-container .notes{\n	font-size:12px;\n}\n\n.wpcf7 .ajax-loader{\n	display:block;\n}\n\n.wpcf7-not-valid-tip{\n	color:#ffffff;\n}\n\n.wpcf7-response-output{\n	color:#ffffff;\n	border-color:#fff!important;\n}\";}'),
