 risk. ', 'inherit', 'closed', 'closed', '', '365-revision-v1', '', '', '2016-04-21 18:25:01', '2016-04-22 02:25:01', '', 365, 'https://local.omega.com/365-revision-v1/', 0, 'revision', '', 0),
